td.script:
//----------------------------------------------
var ie4 = false; if(document.all) { ie4 = true; }
function getObject(id) { if (ie4) { return document.all[id]; } else { return document.getElementById(id); } }
function toggle(link, divId) { var lText = link.innerHTML; var d = getObject(divId);
 if (lText.match("arrow-down")) { link.innerHTML = '<img src="gifs/arrow-right.gif" border="0" width="7" height="7" alt="show">&nbsp;'+divId; d.style.display = 'none'; }
 else { link.innerHTML = '<img src="gifs/arrow-down.gif" border="0" width="7" height="7" alt="hide">&nbsp;<strong>'+divId+'</strong>'; d.style.display = 'block'; } }
