div.script:
  function loginRedirect() {
	    var url_string = '/Xplore/login.jsp';
	    window.location = url_string;
  }
  function showHideDiv(div,show) {
	    var url_string = '/Xplore/login.jsp';
	    window.location = url_string;
  }  
  function addFilter(filter,checked) { 
	  if(checked)
	  {
		  document.getElementById("newsearch").value="partialPref"
	  	  document.getElementById("filter").value=filter;
		// put a large number so that it would not hit the server
		  acompleter.options.minChars=3000;
		  document.getElementById('search_suggestions').style.visibility = 'hidden';
	  }
	  else
	  {
		  document.getElementById("filter").value="";
		  acompleter.options.minChars=3;
		  document.getElementById('search_suggestions').style.visibility = 'visible';
	  }
  }
	function submitForm() {
		document.forms["search_form"].submit();
	}

    	var acompleter=new Ajax.Autocompleter('queryText', 'search_suggestions', '/search/typeahead',
    		{ paramName: 'searchRefinementTerm', frequency: '0.8', minChars: 3, method: 'get',
					parameters: 'searchRefinementMaxResults=20', 
					afterUpdateElement: "return filterQuery('queryText')"
    		});

  function gotoPage(pagenum) {
    var url_string = '/search/freesearchresult.jsp?' + document.getElementById("oqs").value
    + '&' + 'pageNumber' + '='+pagenum;
    location.href = url_string;
  }

  function doSearch(searchTerm) {
    var url_string = '/search/freesearchresult.jsp?' + document.getElementById("oqs").value  + '&'
    + 'queryText' + '='+searchTerm;
   // alert(url_string);
    return url_string;
  }

  function showAbstract(articleNumber, openAbstractParam, prevArticleId, nextArticleId) {
    var url_string = '/search/srchabstract.jsp?'+ document.getElementById("oqs").value
    + '&' + 'arnumber' +'='+ articleNumber;
    url_string = url_string + '&resultAction=ABSTRACT';
   // alert(url_string);
    location.href = url_string;

  }

  function showFreeAbstract(articleNumber, openAbstractParam, prevArticleId, nextArticleId) {
	    var url_string = '/search/freesrchabstract.jsp?'+ document.getElementById("oqs").value
	    + '&' + 'arnumber' +'='+ articleNumber;
	    url_string = url_string + '&resultAction=ABSTRACT';
	   // alert(url_string);
	    location.href = url_string;

	  }

  function sortAction(sortTypeValue) {	
    var sortType = sortTypeValue.value;
    var url_string = '/search/freesearchresult.jsp?' + document.getElementById("oqs").value
    + '&' + 'sortType' + '='+ sortType
    +  '&'  + 'pageNumber' + '='+ '1'+ '&resultAction=SORT';
    location.href = url_string;
  }

  function removeSearchWithin(term) {
	  var filteredTerm = getFilteredTerms(term);
    var url_string = '/search/freesearchresult.jsp?' + document.getElementById("oqs").value + '&'
    + 'removeSearchWithin' + '=' + filteredTerm;
    url_string = url_string 
    +  '&'  + 'pageNumber' + '='+ '1'+ '&resultAction=REFINE';
    location.href = encodeURI(url_string);
  }

  function removeRefinement(refineID) {
    var url_string = '/search/freesearchresult.jsp?'+document.getElementById("oqs").value
    + '&' + 'removeRefinement' + '=' + refineID;
    url_string = url_string + 
     '&'  + 'pageNumber' + '='+ '1'+ '&resultAction=REFINE';
    location.href = url_string;
  }

  function removeRange(key) {
    var url_string = '/search/freesearchresult.jsp?'+document.getElementById("oqs").value
    + '&' + 'removeRange' + '=' + key;
    url_string = url_string 
    +  '&'  + 'pageNumber' + '='+ '1'+ '&resultAction=REFINE';
    location.href = url_string;
  }

  function rowsPerPageAction(rowsPerPageSelect) {
	var  rowsPerPage=rowsPerPageSelect.value;
    var url_string = '/search/freesearchresult.jsp?' + document.getElementById("oqs").value
    + '&' + 'rowsPerPage' + '='+rowsPerPage 
    + '&' + 'pageNumber' + '='+ '1'+ '&resultAction=ROWS_PER_PAGE';
    location.href = url_string;
  }

  function loginRedirect() {
	    var url_string = '/Xplore/login.jsp';
	    window.location = url_string;
}

  function printSelect() {
	  window.print();
	  return false;
}

  function searchWithin() {
	 // 
 	var good_query = filterQuery("swr_query");
 	if (good_query) {
 		var swr_query = getFilteredTerms(document.getElementById("swr_query").value);
 	    var url_string =  '/search/freesearchresult.jsp?' + 
    	document.getElementById("oqs").value +'&'
    	+ 'searchWithin'+ '=' + swr_query;
    	 url_string = url_string 
    	 +  '&'  + 'pageNumber' + '='+ '1'+ '&resultAction=REFINE';
    window.location = encodeURI(url_string);
    } else {
        return;
    }
  }

