new Ajax.Autocompleter(
'swr_query', 'search_within_suggestions', '/search/typeahead',
{ paramName: 'searchRefinementTerm', frequency: '1.0', minChars: 3, method: 'get', parameters: encodeURI('queryText=model driven&openedRefinements=*&rowsPerPage=100&searchField=Search All') + '&searchRefinementId=3344', afterUpdateElement: searchWithin  }
);
