fieldset.script:
var Content_Type_defaultRefinements = $('Content_Type-refinements').childElements();
var Content_Type_selections = $A([]);
var Content_TypeAutocompleter = new IEEERefinementAutocompleter(
  'Content_Type-search-field', 'Content_Type-autocomplete', '/search/typeahead', 
  { method: 'get', paramName: 'searchRefinementTerm', 
    frequency: 0.8, minChars: 2, 
    parameters: 'queryText=model driven&openedRefinements=*&rowsPerPage=100&searchField=Search All&searchRefinementId=3341&addCheckBox=true&refinementName=Content_Type', 
    refinementList: $('Content_Type-refinements'), 
    selectedList: $('Content_Type-selections'), 
    defaultItems: Content_Type_defaultRefinements, 
    selectedItems: Content_Type_selections, 
    selectorClass: 'refinement-selector', 
    refinementClass: 'refinement', 
    clearButtonClass: 'clear-button', 
    updateButtonClass: 'update-button', 
    backgroundColor: '#ffffff', highlightColor: '#ffffcc'
  }
);
Content_TypeAutocompleter.replaceRefinementListContents(Content_Type_defaultRefinements);
$('Content_Type-search-field').observe('keyup', Content_TypeAutocompleter.onKeyUp.bindAsEventListener(Content_TypeAutocompleter));
$('Content_Type-search-field').next('.clear-button').observe('click', Content_TypeAutocompleter.onClearButton.bindAsEventListener(Content_TypeAutocompleter));

